    dat=ichimoku(df)
    labela = go.Scatter(x=dat.index, y=dat['Lead_span_A'], name='label a',
                               line={'color': 'green'})
    labelb= go.Scatter(x=dat.index, y=dat['Lead_span_B'], name="label b",
                               line={'color': 'red'})
    fig4 = go.Figure(data=[fig_ochl, labela, labelb])
    fig4.update_layout(height=int(800))
    fig4.layout.xaxis.type = 'category'
