def elliot_plot(dat):
    fig_ochl=ohcl_chart(dat)
    g=elliot_waves.get_max_min(dat,3,10)
    p=elliot_waves.find_patterns(g)
    fig0 = elliot_waves.plotit(p, g)

    fig4 = go.Figure(data=[fig_ochl,fig0])
    fig4.layout.xaxis.type = 'category'
    fig4.layout.xaxis.rangeslider.visible = True
    fig4.update_layout(height=int(800))
    return fig4

